import React from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const TossChart = ({ data, team1, team2 }) => {
  if (!data || data.length === 0) {
    return (
      <div className="text-center text-gray-500">
        No toss data available for selected teams
      </div>
    );
  }

  // Process data for each team
  const team1Data = data.filter((d) => d._id.winner === team1);
  const team2Data = data.filter((d) => d._id.winner === team2);

  const processTeamData = (teamData) => {
    const batFirst = teamData.find((d) => d._id.decision === "bat") || {
      count: 0,
      matchesWon: 0,
    };
    const fieldFirst = teamData.find((d) => d._id.decision === "field") || {
      count: 0,
      matchesWon: 0,
    };
    return {
      batFirst,
      fieldFirst,
      totalTossWins: batFirst.count + fieldFirst.count,
      totalWinsAfterToss: batFirst.matchesWon + fieldFirst.matchesWon,
    };
  };

  const team1Stats = processTeamData(team1Data);
  const team2Stats = processTeamData(team2Data);

  const chartData = {
    labels: [
      `${team1} (Batting)`,
      `${team1} (Fielding)`,
      `${team2} (Batting)`,
      `${team2} (Fielding)`,
    ],
    datasets: [
      {
        label: "Matches Won",
        data: [
          team1Data.find((d) => d._id.decision === "bat")?.matchesWon || 0,
          team1Data.find((d) => d._id.decision === "field")?.matchesWon || 0,
          team2Data.find((d) => d._id.decision === "bat")?.matchesWon || 0,
          team2Data.find((d) => d._id.decision === "field")?.matchesWon || 0,
        ],
        backgroundColor: "#60A5FA",
      },
      {
        label: "Matches Lost",
        data: [
          (team1Data.find((d) => d._id.decision === "bat")?.count || 0) -
            (team1Data.find((d) => d._id.decision === "bat")?.matchesWon || 0),
          (team1Data.find((d) => d._id.decision === "field")?.count || 0) -
            (team1Data.find((d) => d._id.decision === "field")?.matchesWon ||
              0),
          (team2Data.find((d) => d._id.decision === "bat")?.count || 0) -
            (team2Data.find((d) => d._id.decision === "bat")?.matchesWon || 0),
          (team2Data.find((d) => d._id.decision === "field")?.count || 0) -
            (team2Data.find((d) => d._id.decision === "field")?.matchesWon ||
              0),
        ],
        backgroundColor: "#F87171",
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: "top",
      },
      title: {
        display: true,
        text: "Toss Decision Outcomes by Team",
        font: {
          size: 16,
        },
      },
      tooltip: {
        callbacks: {
          label: function (context) {
            const label = context.dataset.label || "";
            const value = context.parsed.y;
            const datasetIndex = context.datasetIndex;
            const total =
              chartData.datasets[0].data[context.dataIndex] +
              chartData.datasets[1].data[context.dataIndex];
            const percentage = ((value / total) * 100).toFixed(1);
            return `${label}: ${value} (${percentage}%)`;
          },
        },
      },
    },
    scales: {
      x: {
        stacked: true,
        grid: {
          display: false,
        },
      },
      y: {
        stacked: true,
        title: {
          display: true,
          text: "Number of Matches",
        },
      },
    },
  };

  return (
    <div className="space-y-6">
      <Bar data={chartData} options={options} />

      {/* Toss Statistics Summary */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="bg-blue-50 p-4 rounded-lg">
          <h4 className="font-semibold text-blue-800 mb-2">{team1} Stats</h4>
          <div className="space-y-1 text-sm">
            <p>
              Total Toss Wins:{" "}
              <span className="font-medium">{team1Stats.totalTossWins}</span>
            </p>
            <p>
              Win Rate After Winning Toss:{" "}
              <span className="font-medium">
                {team1Stats.totalTossWins
                  ? (
                      (team1Stats.totalWinsAfterToss /
                        team1Stats.totalTossWins) *
                      100
                    ).toFixed(1)
                  : 0}
                %
              </span>
            </p>
            <p>
              Preferred Choice:{" "}
              <span className="font-medium">
                {team1Stats.batFirst.count > team1Stats.fieldFirst.count
                  ? "Batting"
                  : "Fielding"}{" "}
                First
              </span>
            </p>
          </div>
        </div>

        <div className="bg-purple-50 p-4 rounded-lg">
          <h4 className="font-semibold text-purple-800 mb-2">{team2} Stats</h4>
          <div className="space-y-1 text-sm">
            <p>
              Total Toss Wins:{" "}
              <span className="font-medium">{team2Stats.totalTossWins}</span>
            </p>
            <p>
              Win Rate After Winning Toss:{" "}
              <span className="font-medium">
                {team2Stats.totalTossWins
                  ? (
                      (team2Stats.totalWinsAfterToss /
                        team2Stats.totalTossWins) *
                      100
                    ).toFixed(1)
                  : 0}
                %
              </span>
            </p>
            <p>
              Preferred Choice:{" "}
              <span className="font-medium">
                {team2Stats.batFirst.count > team2Stats.fieldFirst.count
                  ? "Batting"
                  : "Fielding"}{" "}
                First
              </span>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TossChart;
