import React from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Bar } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const BatsmenChart = ({ data }) => {
  const chartData = {
    labels: data.map((player) => player.batsman),
    datasets: [
      {
        label: "Total Runs",
        data: data.map((player) => player.totalRuns),
        backgroundColor: "#60A5FA",
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: "top",
      },
      title: {
        display: true,
        text: "Top 5 Batsmen by Runs",
      },
    },
  };

  return <Bar options={options} data={chartData} />;
};

export default BatsmenChart;
