import React from "react";
import axios from "axios";
import { API_URL } from "../config";
import LoadingSpinner from "./LoadingSpinner";

const MatchStatsPopup = ({ match, onClose, isOpen }) => {
  const [loading, setLoading] = React.useState(true);
  const [matchStats, setMatchStats] = React.useState(null);
  const [error, setError] = React.useState(null);

  React.useEffect(() => {
    const fetchMatchStats = async () => {
      try {
        setLoading(true);
        console.log("Fetching stats for match:", match);
        const response = await axios.get(`${API_URL}/match-stats`, {
          params: {
            matchId: match._id,
          },
        });
        console.log("Match stats response:", response.data);
        setMatchStats(response.data);
      } catch (err) {
        console.error("Error fetching match stats:", err);
        console.error("Match object:", match);
        setError("Failed to load match statistics");
      } finally {
        setLoading(false);
      }
    };

    if (isOpen && match) {
      fetchMatchStats();
    }
    return () => {
      // Reset state when component unmounts or match changes
      setMatchStats(null);
      setError(null);
    };
  }, [isOpen, match]);

  if (!isOpen || !match) return null;

  const TeamStats = ({ team, stats, total, isWinner }) =>
    stats && total ? (
      <div
        className={`w-full ${
          isWinner ? "bg-green-50" : "bg-gray-50"
        } p-4 rounded-lg`}
      >
        <h3
          className={`text-lg font-bold mb-2 ${
            isWinner ? "text-green-700" : "text-gray-700"
          }`}
        >
          {team} {isWinner && "(Winner)"}
        </h3>
        <div className="mb-4 bg-white p-3 rounded shadow-sm">
          <p className="text-sm font-semibold">
            Team Total: {total.totalRuns} runs
          </p>
          <p className="text-sm">
            Boundaries: {total.fours} fours, {total.sixes} sixes
          </p>
        </div>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">
                  Batsman
                </th>
                <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">
                  R
                </th>
                <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">
                  B
                </th>
                <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">
                  4s
                </th>
                <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">
                  6s
                </th>
                <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">
                  SR
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {stats.map((batsman) => (
                <tr key={batsman.batsman} className="hover:bg-gray-50">
                  <td className="px-4 py-2 whitespace-nowrap text-sm font-medium">
                    {batsman.batsman}
                  </td>
                  <td className="px-4 py-2 whitespace-nowrap text-sm">
                    {batsman.runs}
                  </td>
                  <td className="px-4 py-2 whitespace-nowrap text-sm">
                    {batsman.balls}
                  </td>
                  <td className="px-4 py-2 whitespace-nowrap text-sm">
                    {batsman.fours}
                  </td>
                  <td className="px-4 py-2 whitespace-nowrap text-sm">
                    {batsman.sixes}
                  </td>
                  <td className="px-4 py-2 whitespace-nowrap text-sm">
                    {batsman.strikeRate.toFixed(2)}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    ) : (
      <div className="text-gray-500">Loading team stats...</div>
    );

  return (
    <div
      className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
      onClick={(e) => {
        if (e.target === e.currentTarget) {
          onClose();
        }
      }}
    >
      <div className="bg-white rounded-lg p-6 max-w-6xl w-full mx-4">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold text-gray-800">Match Statistics</h2>
          <button
            onClick={onClose}
            className="text-gray-500 hover:text-gray-700"
          >
            <svg
              className="w-6 h-6"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M6 18L18 6M6 6l12 12"
              />
            </svg>
          </button>
        </div>

        {loading ? (
          <LoadingSpinner />
        ) : error ? (
          <div className="text-red-500 text-center">{error}</div>
        ) : matchStats && matchStats.team1 && matchStats.team2 ? (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <TeamStats
              team={matchStats.team1.name}
              stats={matchStats.team1.stats}
              total={matchStats.team1.total}
              isWinner={matchStats.winner === matchStats.team1.name}
            />
            <TeamStats
              team={matchStats.team2.name}
              stats={matchStats.team2.stats}
              total={matchStats.team2.total}
              isWinner={matchStats.winner === matchStats.team2.name}
            />
          </div>
        ) : (
          <div className="text-gray-500 text-center">
            No match data available
          </div>
        )}
      </div>
    </div>
  );
};

export default MatchStatsPopup;
